mapper.select:
SELECT usr.*, count(RELATION.sub_id) relation_user_num FROM ( SELECT * FROM uam_user
name like '%${name}%' or user_login like '%${name}%'
) usr LEFT JOIN ( SELECT r.main_id, u.id sub_id FROM webstarter_uam_relation r, uam_user u WHERE type = 'userMgrGroup' AND u.group_id = r.sub_id UNION SELECT r.main_id, r.sub_id FROM webstarter_uam_relation r WHERE type = 'userMgrUser' ) RELATION ON relation.main_id = usr.id GROUP BY id,group_id,user_login,pwd,safe_eval_pwd,name,email,office_phone,mobile_phone,update_time,data_state,show_order order by show_order

mapper.sql:
    from uam_user RES
    
    	1 = 1
 		
			and id = #{id,jdbcType=VARCHAR}
		
		
			and group_id = #{groupId,jdbcType=VARCHAR}
		
		
			and user_login = #{userLogin,jdbcType=VARCHAR}
		
		
   			and RES.id in 
   			(select main_id from webstarter_uam_relation where sub_id in
   			(select id from webstarter_uam_role where name=#{pwd,jdbcType=VARCHAR})
   			 and type='userRole') 
		
		
   			and RES.id in 
   			(select main_id from webstarter_uam_relation where sub_id in
   			(select id from webstarter_uam_role where name=#{safeEvalPwd,jdbcType=VARCHAR})
   			 and type='userRole') 
		
		
			and name like #{name,jdbcType=VARCHAR}
		
		
			and email = #{email,jdbcType=VARCHAR}
		
		
			and office_phone = #{officePhone,jdbcType=VARCHAR}
		
		
			and mobile_phone = #{mobilePhone,jdbcType=VARCHAR}
		
		
			and update_time = #{updateTime,jdbcType=TIMESTAMP}
		
		
			and data_state = #{dataState,jdbcType=VARCHAR}
		
		
			and show_order = #{showOrder,jdbcType=INTEGER}

mapper.select:
SELECT * FROM uam_user
WHERE group_id = #{ groupId,jdbcType = VARCHAR }
order BY show_order
